import { Component, Input } from '@angular/core';
import {Hero} from '../hero'
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

import { HeroService } from '../hero.service';
@Component({
  selector: 'app-hero-detail',
  templateUrl: './hero-detail.component.html',
  styleUrls: ['./hero-detail.component.scss']
})
export class HeroDetailComponent {
  constructor(
    private route: ActivatedRoute, //chua in4 ve route cua herodetailcomponent
    private heroService: HeroService,
    private location: Location //cho phep back lai page before
  ) {}

  @Input() hero?: Hero; // ? cho phép có thể có value|| không cần có value

    ngOnInit(): void{

    }

    getHero(): void {
      const id = Number(this.route.snapshot.paramMap.get('id')) //lay id tren thanh url
      this.heroService.getHeroes(id).subscribe(hero => this.hero = hero)
    }






}
<div *ngIf="hero">
    <h2>{{hero.name | uppercase}} Details</h2>
    <div>id: {{hero.id}}</div>
    <div>
      <label for="hero-name">Hero name: </label>
      <input id="hero-name" [(ngModel)]="hero.name" placeholder="name">
    </div>
  </div>
